import React, {Component} from 'react';
import  CreatePoll from '../new_poll/CreatePoll';
import NewPoll from '../new_poll/NewPoll'
import "./Page.css"

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            form: <CreatePoll/>
        }
    }

    updateContent = () => {
        this.setState({ form: <NewPoll/>});
    }

    render() {
        return (
            <div>
                <div className="h1 bg-secondary text-white text-center p-2">
                    { this.state.form }
                </div>
                <div className="text-center">
                    <button className='new' onClick={this.updateContent}></button>
                </div>
            </div>
        );
    }
}

