import React, {Component} from "react";

class NewPoll extends Component {
    constructor() {
        super();

        this.state = {
            poll_name: "",
        };
    }

    render() {
        return (
            <form>
                <h2>NewPoll</h2>

                <input type="text" class ="box" placeholder="Enter a poll name" name = 'poll_name' value={this.state.poll_name} onChange={this.handleChange}/>
            </form>
        );
    }
}

export default NewPoll;
