import React, {Component} from 'react';
import '../create_poll/CreatePoll.css';
import CreatePoll from '../create_poll/CreatePoll'
import "./Page.css"


class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            form: null,
            option: null
        }
    }

    addPoll = () => {
        this.setState({ form: <CreatePoll/>});
    }

    addOption = () => {
        this.setState({ option: <input type="text" className="poll_box" placeholder="Add option" name='option' value={this.state.option} onChange={this.handleChange}/>});
    }

    render() {
        return (
            <body id="CreateForm">
            <div className="parent-container">
                <div className="add-container">
                    <button className='new' onClick={this.addPoll}></button>
                </div>

                <div className="poll-container">
                    <button type='checkbox' onClick={this.addOption}></button>
                    { this.state.form }
                    { this.state.option }
                </div>
            </div>
            </body>

        );
    }
}

export default Home;
