import React, {Component} from 'react';
import '../new_poll/CreatePoll.css';
import NewPoll from '../new_poll/NewPoll'
import "./Page.css"


class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            form: null
        }
    }

    updateContent = () => {
        this.setState({ form: <NewPoll/>});
    }

    render() {
        return (
            <body id="CreateForm">
            <div className="parent-container">
                <div className="add-container">
                    <button className='new' onClick={this.updateContent}></button>
                </div>

                <div className="poll-container">
                    { this.state.form }
                </div>
            </div>
            </body>

        );
    }
}

export default Home;
