const todos = (state = [], action) => {
    switch (action.type) {
        case 'ADD_TODO':
            return [
                ...state,
                {
                    text: action.text
                }
            ]
        case 'TOGGLE_TODO':
            return state.map(todo => todo
            )
        default:
            return state
    }
}

export default todos
