import React from 'react'
import { Field, FieldArray, reduxForm } from 'redux-form'
import renderField from '../forms/FormValidation'

const renderQuestions = ({ fields }) => (
    <ul>
        <li>
            <button type="button" onClick={() => fields.push({})}>Add Question</button>
        </li>
        {fields.map((question, index) =>
            <li key={index}>
                <button
                    type="button"
                    title="Remove Question"
                    onClick={() => fields.remove(index)}/>
                <h4>Question #{index + 1}</h4>
                <Field
                    name={`${question}.questionName`}
                    type="text"
                    component={renderField}
                    placeholder="Question Name"/>
                <FieldArray name={`${question}.options`} component={renderOptions}/>
            </li>
        )}
    </ul>
)

const renderOptions = ({ fields }) => (
    <ul>
        <li>
            <button type="button" onClick={() => fields.push()}>Add Option</button>
        </li>
        {fields.map((option, index) =>
            <li key={index}>
                <button
                    type="button"
                    title="Remove Option"
                    onClick={() => fields.remove(index)}/>
                <Field
                    name={option}
                    type="text"
                    component={renderField}
                    placeholder={`Option #${index + 1}`}/>
            </li>
        )}
        {fields.error && <li className="error">{fields.error}</li>}
    </ul>
)

const FieldArraysForm = (props) => {
    const { handleSubmit, pristine, reset, submitting } = props
    return (
        <form onSubmit={handleSubmit}>
            <Field name="Poll" type="text" component={renderField} placeholder="Poll Name"/>
            <FieldArray name="questions" component={renderQuestions}/>
            <div>
                <button type="submit" disabled={submitting}>Submit</button>
                <button type="button" disabled={pristine || submitting} onClick={reset}>Clear Values</button>
            </div>
        </form>
    )
}

export default reduxForm({
    form: 'fieldArrays'
})(FieldArraysForm)





