import React, {Component} from 'react'
import './custom.scss';
import PollForm from "../forms/PollForm";

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            form: null
        }
    }

    submit = values => {
        window.alert (JSON.stringify (values));
    };

    addPoll = () => {
        this.setState({ form:  <PollForm onSubmit={this.submit} />
        });
    }

    render() {
        return (
            <div className="container-poll d-flex justify-content-center">
                <div className="form-container d-flex flex-column align-items-center justify-content-center">
                    <div className="add-container">
                        <button type="button" class="btn btn-outline-success" onClick={this.addPoll}>New Poll</button>
                    </div>

                    <div className="container">
                        { this.state.form }
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
