const optionReducer = (state = [], action) => {
    switch (action.type ) {
        case 'ADD_OPTION':
            return [...state,
                {
                    question: action.question
                }
            ]
        case 'TOGGLE_OPTION':
            return state.map(option => option)
        default:
            return state
    }

}

export default optionReducer
